import { useState, useCallback } from 'react';
import Header from '../components/Header';

function PostPage() {
  // 현재 상태, Setter
  const [postContent, setPostContent] = useState('');
  const [postTag, setPostTag] = useState('');
  const [disabled, setDisabled] = useState(false);

  const [tagArr, setTagArr] = useState([]);

  const handleContentChange = ({ target: { value } }) => {
    if (value.length > 50) {
      alert('글자수 초과!!');
    } else {
      setPostContent(value);
    }
  };
  const handleTagChange = ({ target: { value } }) => {
    if (value.length > 10) {
      alert('글자수 초과!!');
    } else {
      setPostTag(value);
    }
  };

  const onKeyUp = useCallback(
    e => {
      if (process.browser) {
        const $TagWrapOuter = document.querySelector('.tagWrapOuter');
        const $TagWrapInner = document.createElement('div');
        $TagWrapInner.className = 'tagWrapInner';

        // $TagWrapInner.addEventListener('click', (e) => {
        //   $TagWrapInner?.removeChild($TagWrapInner);
        //   console.log($TagWrapInner.innerHTML);
        //   setTagArr(tagArr.filter(e => e));
        // });

        if (e.keyCode === 13 && e.target.value.trim() !== '') {
          console.log('Enter key!!!', e.target.value);
          $TagWrapInner.innerHTML = `#${e.target.value}`;
          $TagWrapOuter?.appendChild($TagWrapInner);
          setTagArr([...tagArr, postTag]);
          setPostTag('');
        }
      }
    },
    [postTag, tagArr],
  );

  const handleSubmit = async e => {
    setDisabled(true);
    e.preventDefault();
    await new Promise(r => setTimeout(r, 1000));
    console.log(postContent);
    console.log(postTag);
    setDisabled(false);
  };
  return (
    <>
      <Header />
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="postContent"
          value={postContent}
          onChange={handleContentChange}
        ></input>
        <div className="tagWrap">
          <div className="tagWrapOuter"></div>
          <input
            type="text"
            name="postTag"
            value={postTag}
            onChange={handleTagChange}
            onKeyUp={onKeyUp}
          />
        </div>
        <button type="submit" disabled={disabled}>
          UPLOAD
        </button>
      </form>
    </>
  );
}

export default PostPage;
