import { useState, useEffect } from 'react';
import axios from 'axios';
import Calendar from 'react-calendar';
import '../stylesheets/MainCalendar.css';

const MainCalendar = ({ clickedDay, setClickedDay }) => {
  const [dateVal, setDateVal] = useState(new Date());

  const onClickDay = e => {
    console.log(`${e.getMonth() + 1}월 ${e.getDate()}일 입니다`);
    setClickedDay(`${e.getMonth() + 1}월 ${e.getDate()}일 입니다`);
  };

  const setPostedDay = ({ date, view }) => {
    console.log(view);
    if (date.getDay() % 2 === 0) return <p>hello</p>;
    return;
  };

  // test api
  useEffect(async () => {
    const response = await axios.get(`http://localhost:8000/calendar/dhyeon`);

    console.log(response);
    console.log(response);
  }, []);

  return (
    <>
      <Calendar
        // className="main-calendar"
        onChange={setDateVal}
        value={dateVal}
        maxDate={new Date()}
        locale="en-US"
        onClickDay={onClickDay}
        formatMonthYear={() =>
          new Intl.DateTimeFormat('ko-KR', {
            year: 'numeric',
            month: 'numeric',
          }).format(dateVal)
        }
        tileContent={setPostedDay}
      />
    </>
  );
};

export default MainCalendar;
