import { useEffect, useState } from 'react';
import Header from '../components/Header';
import NaviBar from '../components/NaviBar';

function renderPost({ postContent, tagArr }) {
  return (
    <>
      <div>{postContent}</div>
      <div>{tagArr}</div>
    </>
  );
}

function MainPage() {
  const [post, setPost] = useState([]);

  const getPost = () => {
    if (localStorage.getItem('postContent')) {
      const newPost = {
        postContent: localStorage.getItem('postContent'),
        postTag: localStorage.getItem('tagArr'),
      };
      setPost([...post, newPost]);
      localStorage.removeItem('postContent');
      localStorage.removeItem('tagArr');
    }
  };

  useEffect(() => getPost(), []);

  return (
    <>
      <Header />
      <div>MainPage</div>
      {post.map(p => (
        <renderPost postContent={p.postContent} tagArr={p.tagArr} />
      ))}
      <NaviBar />
    </>
  );
}

export default MainPage;
